char *
task_state_string(ulong task, char *buf, int verbose)
{
long state;
int exclusive;
int valid, set;

if (_RUNNING_ == TASK_STATE_UNINITIALIZED) 
    initialize_task_state();

if (verbose)
    return task_state_string_verbose(task, buf);

if (buf)
    sprintf(buf, verbose ? "(unknown)" : "??");

state = task_state(task);

set = valid = exclusive = 0;
if (valid_task_state(_EXCLUSIVE_)) {
    exclusive = state & _EXCLUSIVE_;
    state &= ~(_EXCLUSIVE_);
}

if (state == _RUNNING_) {
    sprintf(buf, "RU"); 
    valid++;
}

if (state & _INTERRUPTIBLE_) { 
    sprintf(buf, "IN"); 
    valid++; 
    set++;
}

if (state & _UNINTERRUPTIBLE_) {
    if (valid_task_state(_NOLOAD_) &&
        (state & _NOLOAD_))
        sprintf(buf, "ID");
    else
        sprintf(buf, "UN");
    valid++; 
    set++;
}

if (state & _ZOMBIE_) {
    sprintf(buf, "ZO"); 
    valid++; 
    set++;
}

if (state & _STOPPED_) {
    sprintf(buf, "ST"); 
    valid++; 
    set++;
}

if (valid_task_state(_TRACING_STOPPED_) &&
    (state & _TRACING_STOPPED_)) {
    sprintf(buf, "TR"); 
    valid++; 
    set++;
}

if (state == _SWAPPING_) {
    sprintf(buf, "SW"); 
    valid++; 
    set++;
}

if ((state & _DEAD_) && !set) {
    sprintf(buf, "DE"); 
    valid++; 
    set++;
}

if (state == _PARKED_) {
    sprintf(buf, "PA"); 
    valid++;
}

if (state == _WAKING_) {
    sprintf(buf, "WA"); 
    valid++;
}

if (state == _NEW_) {
    sprintf(buf, "NE");
    valid++;
}

if (valid && exclusive) 
    strcat(buf, "EX");

return buf;

